import java.util.Scanner;
public class ForEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
//		break문
//		5까지 출력한다
		
		for (int i = 0; i < 10; i++) {
			System.out.println(i);
			if(i == 5) {
				break;
				
			}
			
		}
		
		
	}
}