package operation2;

public class OperatorEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
//		논리부정 연산자 = !
//		가독성이 떨어짐
//		클린 코드 X
		
		boolean flag = false;
		
		System.out.println(flag);
		flag = !flag;
		System.out.println(flag);
		System.out.println(!flag);
	}

}
